mapper.select:
select GENERATION_IDX, APARTMENT_IDX, TELL, REG_DATE, EMAIL, BUILDING, NUM, ID
from(
	select rownum rnum, g.* 
	from(
		select GENERATION_IDX, APARTMENT_IDX, TELL, REG_DATE, EMAIL, BUILDING, NUM, ID
		from TB_GENERATION 



			APARTMENT_IDX = #{apartmentIdx} and



		APARTMENT_IDX = #{apartmentIdx}
		order by GENERATION_IDX asc
		) g
	) where rnum between #{paging.queryStart} and #{paging.queryEnd}

mapper.update:
  update TB_MANAGER	 


  PASSWORD = #{password}


  , EMAIL = #{email}


  , TELL = #{tell}


	 where MANAGER_IDX = #{managerIdx}
