mapper.select:
select GENERATION_IDX,BUILDING,NUM,ID,PASSWORD,REG_DATE
from(
	select rownum rnum, g.* 
	from(
		select GENERATION_IDX,BUILDING,NUM,ID,PASSWORD,REG_DATE 
		from TB_GENERATION where is_del = 0 and APARTMENT_IDX = #{apartmentIdx}
		order by GENERATION_IDX asc
		) g
	) where rnum between #{paging.queryStart} and #{paging.queryEnd}

mapper.update:
  update TB_MANAGER	 


  PASSWORD = #{password}


  , EMAIL = #{email}


  , TELL = #{tell}


	 where MANAGER_IDX = #{managerIdx}
