mapper.select:
	      select USED_IDX, APARTMENT_IDX,USED_TITLE,USED_CONTENT,USED_WRITER,USED_REG_DATE,IS_DEL,IS_TRNSC,IS_PRIVATE,GENERATION_IDX
      from(
         select rownum rnum, b.*
         from(
            select USED_IDX, APARTMENT_IDX,USED_TITLE,USED_CONTENT,USED_WRITER,USED_REG_DATE,IS_DEL,IS_TRNSC,IS_PRIVATE,GENERATION_IDX
            from TB_USED_BRD where IS_DEL = 0 and APARTMENT_IDX = #{apartmentIdx}
            order by USED_IDX desc
         ) b
      ) where rnum between #{paging.queryStart} and #{paging.queryEnd}
